/// Serve ACP on a given transport (for in-process testing)
pub async fn serve<R, W>(agent: Arc<GooseAcpAgent>, read: R, write: W) -> Result<()>
where
    R: futures::AsyncRead + Unpin + 'static,
    W: futures::AsyncWrite + Unpin + 'static,
{
    let local = tokio::task::LocalSet::new();
    local
        .run_until(async move {
            let client_cell: Arc<std::sync::Mutex<Option<Rc<AgentSideConnection>>>> =
                Arc::new(std::sync::Mutex::new(None));

            let handler = GooseAcpRpcHandler::new(agent, client_cell.clone());

            let (client, io_task) = AgentSideConnection::new(handler, write, read, |fut| {
                tokio::task::spawn_local(fut);
            });

            *client_cell.lock().unwrap() = Some(Rc::new(client));

            io_task.await.map_err(Into::into)
        })
        .await?;

    Ok(())
}

pub async fn run(builtins: Vec<String>) -> Result<()> {
    info!("listening on stdio");

    let outgoing = tokio::io::stdout().compat_write();
    let incoming = tokio::io::stdin().compat();

    let agent = Arc::new(GooseAcpAgent::new(builtins).await?);
    serve(agent, incoming, outgoing).await
}
