107: fn extract_tool_locations(
108:     tool_request: &goose::conversation::message::ToolRequest,
109:     tool_response: &goose::conversation::message::ToolResponse,
110: ) -> Vec<ToolCallLocation> {
111:     let mut locations = Vec::new();
112: 
113:     // Get the tool call details
114:     if let Ok(tool_call) = &tool_request.tool_call {
115:         // Only process text_editor tool
116:         if tool_call.name != "developer__text_editor" {
117:             return locations;
118:         }
119: 
120:         // Extract the path from arguments
121:         let path_str = tool_call
122:             .arguments
123:             .as_ref()
124:             .and_then(|args| args.get("path"))
125:             .and_then(|p| p.as_str());
126: 
127:         if let Some(path_str) = path_str {
128:             // Get the command type
129:             let command = tool_call
130:                 .arguments
131:                 .as_ref()
132:                 .and_then(|args| args.get("command"))
133:                 .and_then(|c| c.as_str());
134: 
135:             // Extract line numbers from the response content
136:             if let Ok(result) = &tool_response.tool_result {
137:                 for content in &result.content {
138:                     if let RawContent::Text(text_content) = &content.raw {
139:                         let text = &text_content.text;
140: 
141:                         // Parse line numbers based on command type and response format
142:                         match command {
143:                             Some("view") => {
144:                                 // For view command, look for "lines X-Y" pattern in header
145:                                 let line = extract_view_line_range(text)
146:                                     .map(|range| range.0 as u32)
147:                                     .or(Some(1));
148:                                 locations.push(create_tool_location(path_str, line));
149:                             }
150:                             Some("str_replace") | Some("insert") => {
151:                                 // For edits, extract the first line number from the snippet
152:                                 let line = extract_first_line_number(text)
153:                                     .map(|l| l as u32)
154:                                     .or(Some(1));
155:                                 locations.push(create_tool_location(path_str, line));
156:                             }
157:                             Some("write") => {
158:                                 // For write, just point to the beginning of the file
159:                                 locations.push(create_tool_location(path_str, Some(1)));
160:                             }
161:                             _ => {
162:                                 // For other commands or unknown, default to line 1
163:                                 locations.push(create_tool_location(path_str, Some(1)));
164:                             }
165:                         }
166:                         break; // Only process first text content
167:                     }
168:                 }
169:             }
170: 
171:             // If we didn't find any locations yet, add a default one
172:             if locations.is_empty() {
173:                 locations.push(create_tool_location(path_str, Some(1)));
174:             }
175:         }
176:     }
177: 
178:     locations
179: }
