181: fn extract_view_line_range(text: &str) -> Option<(usize, usize)> {
182:     // Pattern: "(lines X-Y)" or "(lines X-end)"
183:     let re = regex::Regex::new(r"\(lines (\d+)-(\d+|end)\)").ok()?;
184:     if let Some(caps) = re.captures(text) {
185:         let start = caps.get(1)?.as_str().parse::<usize>().ok()?;
186:         let end = if caps.get(2)?.as_str() == "end" {
187:             start // Use start as a reasonable default
188:         } else {
189:             caps.get(2)?.as_str().parse::<usize>().ok()?
190:         };
191:         return Some((start, end));
192:     }
193:     None
194: }
195: 
196: fn extract_first_line_number(text: &str) -> Option<usize> {
197:     // Pattern: "123: " at the start of a line within a code block
198:     let re = regex::Regex::new(r"```[^\n]*\n(\d+):").ok()?;
199:     if let Some(caps) = re.captures(text) {
200:         return caps.get(1)?.as_str().parse::<usize>().ok();
201:     }
202:     None
203: }
