205: fn read_resource_link(link: ResourceLink) -> Option<String> {
206:     let url = Url::parse(&link.uri).ok()?;
207:     if url.scheme() == "file" {
208:         let path = url.to_file_path().ok()?;
209:         let contents = fs::read_to_string(&path).ok()?;
210: 
211:         Some(format!(
212:             "\n\n# {}\n```\n{}\n```",
213:             path.to_string_lossy(),
214:             contents
215:         ))
216:     } else {
217:         None
218:     }
219: }
