221: fn format_tool_name(tool_name: &str) -> String {
222:     if let Some((extension, tool)) = tool_name.split_once("__") {
223:         let formatted_extension = extension.replace('_', " ");
224:         let formatted_tool = tool.replace('_', " ");
225: 
226:         // Capitalize first letter of each word
227:         let capitalize = |s: &str| {
228:             s.split_whitespace()
229:                 .map(|word| {
230:                     let mut chars = word.chars();
231:                     match chars.next() {
232:                         None => String::new(),
233:                         Some(first) => first.to_uppercase().collect::<String>() + chars.as_str(),
234:                     }
235:                 })
236:                 .collect::<Vec<_>>()
237:                 .join(" ")
238:         };
239: 
240:         format!(
241:             "{}: {}",
242:             capitalize(&formatted_extension),
243:             capitalize(&formatted_tool)
244:         )
245:     } else {
246:         // Fallback for tools without double underscore
247:         let formatted = tool_name.replace('_', " ");
248:         formatted
249:             .split_whitespace()
250:             .map(|word| {
251:                 let mut chars = word.chars();
252:                 match chars.next() {
253:                     None => String::new(),
254:                     Some(first) => first.to_uppercase().collect::<String>() + chars.as_str(),
255:                 }
256:             })
257:             .collect::<Vec<_>>()
258:             .join(" ")
259:     }
260: }
