349:     fn convert_acp_prompt_to_message(&self, prompt: Vec<ContentBlock>) -> Message {
350:         let mut user_message = Message::user();
351: 
352:         // Process all content blocks from the prompt
353:         for block in prompt {
354:             match block {
355:                 ContentBlock::Text(text) => {
356:                     user_message = user_message.with_text(&text.text);
357:                 }
358:                 ContentBlock::Image(image) => {
359:                     // Goose supports images via base64 encoded data
360:                     // The ACP ImageContent has data as a String directly
361:                     user_message = user_message.with_image(&image.data, &image.mime_type);
362:                 }
363:                 ContentBlock::Resource(resource) => {
364:                     // Embed resource content as text with context
365:                     match &resource.resource {
366:                         EmbeddedResourceResource::TextResourceContents(text_resource) => {
367:                             let header = format!("--- Resource: {} ---\n", text_resource.uri);
368:                             let content = format!("{}{}\n---\n", header, text_resource.text);
369:                             user_message = user_message.with_text(&content);
370:                         }
371:                         _ => {
372:                             // Ignore non-text resources for now
373:                         }
374:                     }
375:                 }
376:                 ContentBlock::ResourceLink(link) => {
377:                     if let Some(text) = read_resource_link(link) {
378:                         user_message = user_message.with_text(text)
379:                     }
380:                 }
381:                 ContentBlock::Audio(..) => (),
382:                 _ => (), // Handle any future ContentBlock variants
383:             }
384:         }
385: 
386:         user_message
387:     }
