410:             MessageContent::ToolResponse(tool_response) => {
411:                 self.handle_tool_response(tool_response, session_id, session, cx)
412:                     .await?;
413:             }
414:             MessageContent::Thinking(thinking) => {
415:                 // Stream thinking/reasoning content as thought chunks
416:                 cx.send_notification(SessionNotification::new(
417:                     session_id.clone(),
418:                     SessionUpdate::AgentThoughtChunk(ContentChunk::new(ContentBlock::Text(
419:                         TextContent::new(thinking.thinking.clone()),
420:                     ))),
421:                 ))?;
422:             }
423:             MessageContent::ActionRequired(action_required) => {
424:                 if let ActionRequiredData::ToolConfirmation {
425:                     id,
426:                     tool_name,
427:                     arguments,
428:                     prompt,
429:                 } = &action_required.data
430:                 {
431:                     self.handle_tool_permission_request(
432:                         id.clone(),
433:                         tool_name.clone(),
434:                         arguments.clone(),
435:                         prompt.clone(),
436:                         session_id,
437:                         cx,
438:                     )?;
