448:     async fn handle_tool_request(
449:         &self,
450:         tool_request: &goose::conversation::message::ToolRequest,
451:         session_id: &SessionId,
452:         session: &mut GooseAcpSession,
453:         cx: &JrConnectionCx<AgentToClient>,
454:     ) -> Result<(), sacp::Error> {
455:         // Store the tool request for later use in response handling
456:         session
457:             .tool_requests
458:             .insert(tool_request.id.clone(), tool_request.clone());
459: 
460:         // Extract tool name from the ToolCall if successful
461:         let tool_name = match &tool_request.tool_call {
462:             Ok(tool_call) => tool_call.name.to_string(),
463:             Err(_) => "error".to_string(),
464:         };
465: 
466:         // Send tool call notification using the provider's tool call ID directly
467:         cx.send_notification(SessionNotification::new(
468:             session_id.clone(),
469:             SessionUpdate::ToolCall(
470:                 ToolCall::new(
471:                     ToolCallId::new(tool_request.id.clone()),
472:                     format_tool_name(&tool_name),
473:                 )
474:                 .status(ToolCallStatus::Pending),
475:             ),
476:         ))?;
477: 
478:         Ok(())
479:     }
