481:     async fn handle_tool_response(
482:         &self,
483:         tool_response: &goose::conversation::message::ToolResponse,
484:         session_id: &SessionId,
485:         session: &mut GooseAcpSession,
486:         cx: &JrConnectionCx<AgentToClient>,
487:     ) -> Result<(), sacp::Error> {
488:         let status = match &tool_response.tool_result {
489:             Ok(result) if result.is_error == Some(true) => ToolCallStatus::Failed,
490:             Ok(_) => ToolCallStatus::Completed,
491:             Err(_) => ToolCallStatus::Failed,
492:         };
493: 
494:         let content = build_tool_call_content(&tool_response.tool_result);
495: 
496:         // Extract locations from the tool request and response
497:         let locations = if let Some(tool_request) = session.tool_requests.get(&tool_response.id) {
498:             extract_tool_locations(tool_request, tool_response)
499:         } else {
500:             Vec::new()
501:         };
502: 
503:         // Send status update using provider's tool call ID directly
504:         let mut fields = ToolCallUpdateFields::new().status(status).content(content);
505:         if !locations.is_empty() {
506:             fields = fields.locations(locations);
507:         }
508:         cx.send_notification(SessionNotification::new(
509:             session_id.clone(),
510:             SessionUpdate::ToolCallUpdate(ToolCallUpdate::new(
511:                 ToolCallId::new(tool_response.id.clone()),
512:                 fields,
513:             )),
514:         ))?;
515: 
516:         Ok(())
517:     }
518: 
519:     fn handle_tool_permission_request(
520:         &self,
521:         request_id: String,
522:         tool_name: String,
523:         arguments: serde_json::Map<String, serde_json::Value>,
524:         prompt: Option<String>,
525:         session_id: &SessionId,
526:         cx: &JrConnectionCx<AgentToClient>,
527:     ) -> Result<(), sacp::Error> {
528:         let cx = cx.clone();
529:         let agent = self.agent.clone();
530:         let session_id = session_id.clone();
531: 
532:         let formatted_name = format_tool_name(&tool_name);
533: 
534:         // Use the request_id (provider's tool call ID) directly
535:         let mut fields = ToolCallUpdateFields::new()
536:             .title(formatted_name)
537:             .kind(ToolKind::default())
538:             .status(ToolCallStatus::Pending)
539:             .raw_input(serde_json::Value::Object(arguments));
540:         if let Some(p) = prompt {
541:             fields = fields.content(vec![ToolCallContent::Content(Content::new(
542:                 ContentBlock::Text(TextContent::new(p)),
543:             ))]);
544:         }
545:         let tool_call_update = ToolCallUpdate::new(ToolCallId::new(request_id.clone()), fields);
546: 
547:         fn option(kind: PermissionOptionKind) -> PermissionOption {
548:             let id = serde_json::to_value(kind)
549:                 .unwrap()
550:                 .as_str()
551:                 .unwrap()
552:                 .to_string();
553:             PermissionOption::new(id.clone(), id, kind)
554:         }
555:         let options = vec![
556:             option(PermissionOptionKind::AllowAlways),
557:             option(PermissionOptionKind::AllowOnce),
558:             option(PermissionOptionKind::RejectOnce),
559:             option(PermissionOptionKind::RejectAlways),
560:         ];
561: 
562:         let permission_request =
563:             RequestPermissionRequest::new(session_id, tool_call_update, options);
564: 
565:         cx.send_request(permission_request)
566:             .on_receiving_result(move |result| async move {
567:                 match result {
568:                     Ok(response) => {
569:                         agent
570:                             .handle_confirmation(
571:                                 request_id,
572:                                 outcome_to_confirmation(&response.outcome),
573:                             )
574:                             .await;
575:                         Ok(())
576:                     }
577:                     Err(e) => {
578:                         error!(error = ?e, "permission request failed");
579:                         agent
580:                             .handle_confirmation(
581:                                 request_id,
582:                                 PermissionConfirmation {
583:                                     principal_type: PrincipalType::Tool,
584:                                     permission: Permission::Cancel,
585:                                 },
586:                             )
587:                             .await;
588:                         Ok(())
589:                     }
590:                 }
591:             })?;
592: 
593:         Ok(())
594:     }
595: }
596: 
597: fn outcome_to_confirmation(outcome: &RequestPermissionOutcome) -> PermissionConfirmation {
598:     let permission = match outcome {
599:         RequestPermissionOutcome::Cancelled => Permission::Cancel,
600:         RequestPermissionOutcome::Selected(selected) => {
601:             match serde_json::from_value::<PermissionOptionKind>(serde_json::Value::String(
602:                 selected.option_id.0.to_string(),
603:             )) {
604:                 Ok(PermissionOptionKind::AllowAlways) => Permission::AlwaysAllow,
605:                 Ok(PermissionOptionKind::AllowOnce) => Permission::AllowOnce,
606:                 Ok(PermissionOptionKind::RejectOnce) => Permission::DenyOnce,
607:                 Ok(PermissionOptionKind::RejectAlways) => Permission::AlwaysDeny,
608:                 Ok(_) => Permission::Cancel, // Handle any future permission kinds
609:                 Err(_) => Permission::Cancel,
610:             }
611:         }
612:         _ => Permission::Cancel, // Handle any future variants
613:     };
614:     PermissionConfirmation {
615:         principal_type: PrincipalType::Tool,
616:         permission,
617:     }
618: }
619: 
620: fn build_tool_call_content(tool_result: &ToolResult<CallToolResult>) -> Vec<ToolCallContent> {
621:     match tool_result {
622:         Ok(result) => result
623:             .content
624:             .iter()
625:             .filter_map(|content| match &content.raw {
626:                 RawContent::Text(val) => Some(ToolCallContent::Content(Content::new(
627:                     ContentBlock::Text(TextContent::new(val.text.clone())),
628:                 ))),
629:                 RawContent::Image(val) => Some(ToolCallContent::Content(Content::new(
630:                     ContentBlock::Image(ImageContent::new(val.data.clone(), val.mime_type.clone())),
631:                 ))),
632:                 RawContent::Resource(val) => {
633:                     let resource = match &val.resource {
634:                         ResourceContents::TextResourceContents {
635:                             mime_type,
636:                             text,
637:                             uri,
638:                             ..
639:                         } => EmbeddedResourceResource::TextResourceContents(
640:                             TextResourceContents::new(text.clone(), uri.clone())
641:                                 .mime_type(mime_type.clone()),
642:                         ),
643:                         ResourceContents::BlobResourceContents {
644:                             mime_type,
645:                             blob,
646:                             uri,
647:                             ..
648:                         } => EmbeddedResourceResource::BlobResourceContents(
649:                             BlobResourceContents::new(blob.clone(), uri.clone())
650:                                 .mime_type(mime_type.clone()),
651:                         ),
652:                     };
653:                     Some(ToolCallContent::Content(Content::new(
654:                         ContentBlock::Resource(EmbeddedResource::new(resource)),
655:                     )))
656:                 }
657:                 RawContent::Audio(_) => {
658:                     // Audio content is not supported in ACP ContentBlock, skip it
659:                     None
660:                 }
661:                 RawContent::ResourceLink(_) => {
662:                     // ResourceLink content is not supported in ACP ContentBlock, skip it
663:                     None
664:                 }
665:             })
666:             .collect(),
667:         Err(_) => Vec::new(),
668:     }
669: }
