671: impl GooseAcpAgent {
672:     async fn on_initialize(
673:         &self,
674:         args: InitializeRequest,
675:     ) -> Result<InitializeResponse, sacp::Error> {
676:         debug!(?args, "initialize request");
677: 
678:         // Advertise Goose's capabilities
679:         let capabilities = AgentCapabilities::new()
680:             .load_session(true)
681:             .prompt_capabilities(
682:                 PromptCapabilities::new()
683:                     .image(true)
684:                     .audio(false)
685:                     .embedded_context(true),
686:             )
687:             .mcp_capabilities(McpCapabilities::new().http(true));
688:         Ok(InitializeResponse::new(args.protocol_version).agent_capabilities(capabilities))
689:     }
690: 
691:     async fn on_new_session(
692:         &self,
693:         args: NewSessionRequest,
694:     ) -> Result<NewSessionResponse, sacp::Error> {
695:         debug!(?args, "new session request");
696: 
697:         let manager = self.agent.config.session_manager.clone();
698:         let goose_session = manager
699:             .create_session(
700:                 std::env::current_dir().unwrap_or_default(),
701:                 "ACP Session".to_string(), // just an initial name - may be replaced by maybe_update_name
702:                 SessionType::User,
703:             )
704:             .await
705:             .map_err(|e| {
706:                 sacp::Error::internal_error().data(format!("Failed to create session: {}", e))
707:             })?;
708:         self.update_session_with_provider(&goose_session).await?;
709: 
710:         // Add MCP servers specified in the session request
711:         for mcp_server in args.mcp_servers {
712:             let config = match mcp_server_to_extension_config(mcp_server) {
713:                 Ok(c) => c,
714:                 Err(msg) => {
715:                     return Err(sacp::Error::invalid_params().data(msg));
716:                 }
717:             };
718:             let name = config.name().to_string();
719:             if let Err(e) = self.agent.add_extension(config).await {
720:                 return Err(sacp::Error::internal_error()
721:                     .data(format!("Failed to add MCP server '{}': {}", name, e)));
722:             }
723:         }
724: 
725:         let session = GooseAcpSession {
726:             messages: Conversation::new_unvalidated(Vec::new()),
727:             tool_requests: HashMap::new(),
728:             cancel_token: None,
729:         };
730: 
731:         let mut sessions = self.sessions.lock().await;
732:         sessions.insert(goose_session.id.clone(), session);
733: 
734:         info!(
735:             session_id = %goose_session.id,
736:             session_type = "acp",
737:             "Session started"
738:         );
739: 
740:         Ok(NewSessionResponse::new(SessionId::new(goose_session.id)))
741:     }
742: 
743:     async fn update_session_with_provider(
744:         &self,
745:         goose_session: &Session,
746:     ) -> Result<(), sacp::Error> {
747:         self.agent
748:             .update_provider(self.provider.clone(), &goose_session.id)
749:             .await
750:             .map_err(|e| {
751:                 sacp::Error::internal_error().data(format!("Failed to set provider: {}", e))
752:             })?;
753:         Ok(())
754:     }
755: 
756:     async fn on_load_session(
757:         &self,
758:         args: LoadSessionRequest,
759:         cx: &JrConnectionCx<AgentToClient>,
760:     ) -> Result<LoadSessionResponse, sacp::Error> {
761:         debug!(?args, "load session request");
762: 
763:         let session_id = args.session_id.0.to_string();
764: 
765:         let manager = self.agent.config.session_manager.clone();
766:         let goose_session = manager.get_session(&session_id, true).await.map_err(|e| {
767:             sacp::Error::invalid_params()
768:                 .data(format!("Failed to load session {}: {}", session_id, e))
769:         })?;
770:         self.update_session_with_provider(&goose_session).await?;
771: 
772:         let conversation = goose_session.conversation.ok_or_else(|| {
773:             sacp::Error::internal_error()
774:                 .data(format!("Session {} has no conversation data", session_id))
775:         })?;
776: 
777:         manager
778:             .update(&session_id)
779:             .working_dir(args.cwd.clone())
780:             .apply()
781:             .await
782:             .map_err(|e| {
783:                 sacp::Error::internal_error()
784:                     .data(format!("Failed to update session working directory: {}", e))
785:             })?;
786: 
787:         let mut session = GooseAcpSession {
788:             messages: conversation.clone(),
789:             tool_requests: HashMap::new(),
790:             cancel_token: None,
791:         };
792: 
793:         // Replay conversation history to client
794:         for message in conversation.messages() {
795:             // Only replay user-visible messages
796:             if !message.metadata.user_visible {
797:                 continue;
798:             }
799: 
800:             for content_item in &message.content {
801:                 match content_item {
802:                     MessageContent::Text(text) => {
803:                         let chunk = ContentChunk::new(ContentBlock::Text(TextContent::new(
804:                             text.text.clone(),
805:                         )));
806:                         let update = match message.role {
807:                             Role::User => SessionUpdate::UserMessageChunk(chunk),
808:                             Role::Assistant => SessionUpdate::AgentMessageChunk(chunk),
809:                         };
810:                         cx.send_notification(SessionNotification::new(
811:                             args.session_id.clone(),
812:                             update,
813:                         ))?;
814:                     }
815:                     MessageContent::ToolRequest(tool_request) => {
816:                         self.handle_tool_request(tool_request, &args.session_id, &mut session, cx)
817:                             .await?;
818:                     }
819:                     MessageContent::ToolResponse(tool_response) => {
820:                         self.handle_tool_response(
821:                             tool_response,
822:                             &args.session_id,
823:                             &mut session,
824:                             cx,
825:                         )
826:                         .await?;
827:                     }
828:                     MessageContent::Thinking(thinking) => {
829:                         cx.send_notification(SessionNotification::new(
830:                             args.session_id.clone(),
831:                             SessionUpdate::AgentThoughtChunk(ContentChunk::new(
832:                                 ContentBlock::Text(TextContent::new(thinking.thinking.clone())),
833:                             )),
834:                         ))?;
835:                     }
836:                     _ => {
837:                         // Ignore other content types
838:                     }
839:                 }
840:             }
841:         }
842: 
843:         let mut sessions = self.sessions.lock().await;
844:         sessions.insert(session_id.clone(), session);
845: 
846:         info!(
847:             session_id = %session_id,
848:             session_type = "acp",
849:             "Session loaded"
850:         );
851: 
852:         Ok(LoadSessionResponse::new())
853:     }
854: 
855:     async fn on_prompt(
856:         &self,
857:         args: PromptRequest,
858:         cx: &JrConnectionCx<AgentToClient>,
859:     ) -> Result<PromptResponse, sacp::Error> {
860:         let session_id = args.session_id.0.to_string();
861:         let cancel_token = CancellationToken::new();
862: 
863:         {
864:             let mut sessions = self.sessions.lock().await;
865:             let session = sessions.get_mut(&session_id).ok_or_else(|| {
866:                 sacp::Error::invalid_params().data(format!("Session not found: {}", session_id))
867:             })?;
868:             session.cancel_token = Some(cancel_token.clone());
869:         }
870: 
871:         let user_message = self.convert_acp_prompt_to_message(args.prompt);
872: 
873:         let session_config = SessionConfig {
874:             id: session_id.clone(),
875:             schedule_id: None,
876:             max_turns: None,
877:             retry_config: None,
878:         };
879: 
880:         let mut stream = self
881:             .agent
882:             .reply(user_message, session_config, Some(cancel_token.clone()))
883:             .await
884:             .map_err(|e| {
885:                 sacp::Error::internal_error().data(format!("Error getting agent reply: {}", e))
886:             })?;
887: 
888:         use futures::StreamExt;
889: 
890:         let mut was_cancelled = false;
891: 
892:         while let Some(event) = stream.next().await {
893:             if cancel_token.is_cancelled() {
894:                 was_cancelled = true;
895:                 break;
896:             }
897: 
898:             match event {
899:                 Ok(goose::agents::AgentEvent::Message(message)) => {
900:                     let mut sessions = self.sessions.lock().await;
901:                     let session = sessions.get_mut(&session_id).ok_or_else(|| {
902:                         sacp::Error::invalid_params()
903:                             .data(format!("Session not found: {}", session_id))
904:                     })?;
905: 
906:                     session.messages.push(message.clone());
907: 
908:                     for content_item in &message.content {
909:                         self.handle_message_content(content_item, &args.session_id, session, cx)
910:                             .await?;
911:                     }
912:                 }
913:                 Ok(_) => {}
914:                 Err(e) => {
915:                     return Err(sacp::Error::internal_error()
916:                         .data(format!("Error in agent response stream: {}", e)));
917:                 }
918:             }
919:         }
920: 
921:         let mut sessions = self.sessions.lock().await;
922:         if let Some(session) = sessions.get_mut(&session_id) {
923:             session.cancel_token = None;
924:         }
925: 
926:         Ok(PromptResponse::new(if was_cancelled {
927:             StopReason::Cancelled
928:         } else {
929:             StopReason::EndTurn
930:         }))
931:     }
932: 
933:     async fn on_cancel(&self, args: CancelNotification) -> Result<(), sacp::Error> {
934:         debug!(?args, "cancel request");
935: 
936:         let session_id = args.session_id.0.to_string();
937:         let mut sessions = self.sessions.lock().await;
938: 
939:         if let Some(session) = sessions.get_mut(&session_id) {
940:             if let Some(ref token) = session.cancel_token {
941:                 info!(session_id = %session_id, "prompt cancelled");
942:                 token.cancel();
943:             }
944:         } else {
945:             warn!(session_id = %session_id, "cancel request for unknown session");
946:         }
947: 
948:         Ok(())
949:     }
950: }
