952: pub struct GooseAcpHandler {
953:     pub agent: Arc<GooseAcpAgent>,
954: }
955: 
956: impl JrMessageHandler for GooseAcpHandler {
957:     type Link = AgentToClient;
958: 
959:     fn describe_chain(&self) -> impl std::fmt::Debug {
960:         "goose-acp"
961:     }
962: 
963:     async fn handle_message(
964:         &mut self,
965:         message: MessageCx,
966:         cx: JrConnectionCx<AgentToClient>,
967:     ) -> Result<Handled<MessageCx>, sacp::Error> {
968:         use crate::proto::util::MatchMessageFrom;
969:         use crate::proto::JrRequestCx;
970: 
971:         MatchMessageFrom::new(message, &cx)
972:             .if_request(
973:                 |req: InitializeRequest, req_cx: JrRequestCx<InitializeResponse>| async {
974:                     req_cx.respond(self.agent.on_initialize(req).await?)
975:                 },
976:             )
977:             .await
978:             .if_request(
979:                 |_req: AuthenticateRequest, req_cx: JrRequestCx<AuthenticateResponse>| async {
980:                     req_cx.respond(AuthenticateResponse::new())
981:                 },
982:             )
983:             .await
984:             .if_request(
985:                 |req: NewSessionRequest, req_cx: JrRequestCx<NewSessionResponse>| async {
986:                     req_cx.respond(self.agent.on_new_session(req).await?)
987:                 },
988:             )
989:             .await
990:             .if_request(
991:                 |req: LoadSessionRequest, req_cx: JrRequestCx<LoadSessionResponse>| async {
992:                     req_cx.respond(self.agent.on_load_session(req, &cx).await?)
993:                 },
994:             )
995:             .await
996:             .if_request(
997:                 |req: PromptRequest, req_cx: JrRequestCx<PromptResponse>| async {
998:                     // Spawn the prompt processing in a task so we don't block the event loop.
999:                     // This allows permission responses to be processed while the agent is working.
1000:                     let agent = self.agent.clone();
1001:                     let cx_clone = cx.clone();
1002:                     cx.spawn(async move {
1003:                         match agent.on_prompt(req, &cx_clone).await {
1004:                             Ok(response) => {
1005:                                 req_cx.respond(response)?;
1006:                             }
1007:                             Err(e) => {
1008:                                 req_cx.respond_with_error(e)?;
1009:                             }
1010:                         }
1011:                         Ok(())
1012:                     })?;
1013:                     Ok(())
1014:                 },
1015:             )
1016:             .await
1017:             .if_notification(|notif: CancelNotification| async {
1018:                 self.agent.on_cancel(notif).await
1019:             })
1020:             .await
1021:             .done()
1022:     }
1023: }
