1025: /// Serve ACP on a given transport (for in-process testing)
1026: pub async fn serve<R, W>(agent: Arc<GooseAcpAgent>, read: R, write: W) -> Result<()>
1027: where
1028:     R: futures::AsyncRead + Unpin + Send + 'static,
1029:     W: futures::AsyncWrite + Unpin + Send + 'static,
1030: {
1031:     let handler = GooseAcpHandler { agent };
1032: 
1033:     AgentToClient::builder()
1034:         .name("goose-acp")
1035:         .with_handler(handler)
1036:         .serve(ByteStreams::new(write, read))
1037:         .await?;
1038: 
1039:     Ok(())
1040: }
