1042: pub async fn run(builtins: Vec<String>) -> Result<()> {
1043:     info!("listening on stdio");
1044: 
1045:     let outgoing = tokio::io::stdout().compat_write();
1046:     let incoming = tokio::io::stdin().compat();
1047: 
1048:     let agent = Arc::new(GooseAcpAgent::new(builtins).await?);
1049:     serve(agent, incoming, outgoing).await
1050: }
