189: async fn wait_for(updates: &Arc<Mutex<Vec<SessionNotification>>>, expected: &SessionUpdate) {
190:     let deadline = tokio::time::Instant::now() + Duration::from_millis(500);
191:     let mut context = String::new();
192: 
193:     loop {
194:         let matched = {
195:             let guard = updates.lock().unwrap();
196:             context.clear();
197: 
198:             match expected {
199:                 SessionUpdate::AgentMessageChunk(chunk) => {
200:                     let expected_text = match &chunk.content {
201:                         ContentBlock::Text(t) => &t.text,
202:                         other => panic!("wait_for: unhandled content {:?}", other),
203:                     };
204:                     for n in guard.iter() {
205:                         if let SessionUpdate::AgentMessageChunk(c) = &n.update {
206:                             if let ContentBlock::Text(t) = &c.content {
207:                                 if t.text.is_empty() {
208:                                     context.clear();
209:                                 } else {
210:                                     context.push_str(&t.text);
211:                                 }
212:                             }
213:                         }
214:                     }
215:                     context.contains(expected_text)
216:                 }
217:                 SessionUpdate::ToolCallUpdate(expected_update) => {
218:                     for n in guard.iter() {
219:                         if let SessionUpdate::ToolCallUpdate(u) = &n.update {
220:                             context.push_str(&format!("{:?}\n", u));
221:                             if u.fields.status == expected_update.fields.status {
222:                                 return;
223:                             }
224:                         }
225:                     }
226:                     false
227:                 }
228:                 other => panic!("wait_for: unhandled update {:?}", other),
229:             }
230:         };
231: 
232:         if matched {
233:             return;
234:         }
235:         if tokio::time::Instant::now() > deadline {
236:             panic!("Timeout waiting for {:?}\n\n{}", expected, context);
237:         }
238:         tokio::task::yield_now().await;
239:     }
240: }
